package com.example.capstone.adapterimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport com.bumptech.glide.Glideimport com.example.capstone.databinding.ItemInfoBindingimport com.example.capstone.model.info_model.InfoModelclass InfoAdapter(private var listInfo : ArrayList<InfoModel>) : RecyclerView.Adapter<InfoAdapter.GithubViewHolder>() {    class GithubViewHolder (val binding: ItemInfoBinding) : RecyclerView.ViewHolder(binding.root)    private lateinit var onItemClikCallBack : OnItemClickCallback    fun setOnItemClickCallback(onItemClickCallback: OnItemClickCallback){        this.onItemClikCallBack = onItemClickCallback    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): GithubViewHolder {        return GithubViewHolder(ItemInfoBinding.inflate(LayoutInflater.from(parent.context),parent,false))    }    override fun onBindViewHolder(holder: GithubViewHolder, position: Int) {        holder.binding.apply {            holder.itemView.setOnClickListener {                onItemClikCallBack.onItemClicked(listInfo[position])            }            tvNameEvent.text = listInfo[position].title            Glide.with(holder.itemView.context)                .load(listInfo[position].image)                .circleCrop()                .into(holder.binding.ivEventList)        }    }    override fun getItemCount(): Int = listInfo.size    interface OnItemClickCallback{        fun onItemClicked(data : InfoModel)    }}