package com.example.capstone.adapterimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport com.example.capstone.databinding.ItemEventBindingimport com.example.capstone.databinding.ItemJoinBindingimport com.example.capstone.model.DetailJoinclass JoinEventAdapter(private val listJoin: ArrayList<DetailJoin>): RecyclerView.Adapter<JoinEventAdapter.JoinEventViewHolder>() {    class JoinEventViewHolder(val binding: ItemJoinBinding): RecyclerView.ViewHolder(binding.root)    private lateinit var onItemClickCallback: OnItemClickCallBack    fun setOnItemClickCallback(onItemClickCallback: OnItemClickCallBack){        this.onItemClickCallback = onItemClickCallback    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): JoinEventAdapter.JoinEventViewHolder {        return JoinEventViewHolder(ItemJoinBinding.inflate(LayoutInflater.from(parent.context),parent,false))    }    override fun onBindViewHolder(holder: JoinEventAdapter.JoinEventViewHolder, position: Int) {       holder.binding.apply {           holder.itemView.setOnClickListener{               onItemClickCallback.onItemClicked(listJoin[position])           }           tvUsername.text = listJoin[position].nama           tvDetaulAddress.text = listJoin[position].alamat           tvDetailPhoneUser.text = listJoin[position].phone       }    }    override fun getItemCount(): Int = listJoin.size    interface OnItemClickCallBack{        fun onItemClicked(data: DetailJoin)    }}