package com.example.capstone.adapterimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport com.bumptech.glide.Glideimport com.example.capstone.databinding.ItemCommentBindingimport com.example.capstone.model.event_model.Commentimport com.example.capstone.util.Constantaimport java.util.*class ListCommentAdapter(private val listCommnet: ArrayList<Comment>) :    RecyclerView.Adapter<ListCommentAdapter.CommentViewHolder>() {    class CommentViewHolder(val binding: ItemCommentBinding) : RecyclerView.ViewHolder(binding.root)    private lateinit var onItemClickCallBack: OnItemClickCallBack    fun setOnItemClickCallback(onItemClickCallBack: OnItemClickCallBack) {        this.onItemClickCallBack = onItemClickCallBack    }    override fun onCreateViewHolder(        parent: ViewGroup,        viewType: Int    ): ListCommentAdapter.CommentViewHolder {        return CommentViewHolder(            ItemCommentBinding.inflate(                LayoutInflater.from(parent.context),                parent,                false            )        )    }    override fun onBindViewHolder(holder: ListCommentAdapter.CommentViewHolder, position: Int) {        holder.binding.apply {            holder.itemView.setOnClickListener {                onItemClickCallBack.onItemClicked(listCommnet[position])            }            tvCreated.text =                Constanta.formatDate(listCommnet[position].createdAt, TimeZone.getDefault().id)            tvComment.text = listCommnet[position].commentar            tvUsernameComment.text = listCommnet[position].user.name            Glide.with(holder.itemView.context)                .load(listCommnet[position].user.image_profile)                .circleCrop()                .into(ivAvatarUser)        }    }    override fun getItemCount(): Int = listCommnet.size    interface OnItemClickCallBack {        fun onItemClicked(data: Comment)    }}