package com.example.capstone.adapterimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport com.bumptech.glide.Glideimport com.example.capstone.databinding.ItemEventBindingimport com.example.capstone.model.event_model.Greeventsimport com.example.capstone.util.Constantaimport java.util.*import kotlin.collections.ArrayListclass ListEventsAdapter(private val listEven: ArrayList<Greevents>) :    RecyclerView.Adapter<ListEventsAdapter.ListEventViewHolder>() {    private lateinit var onItemClickCallBack: OnItemClickCallback    fun setOnItemClickCallback(onItemClickCallback: OnItemClickCallback) {        this.onItemClickCallBack = onItemClickCallback    }    class ListEventViewHolder(val binding: ItemEventBinding) : RecyclerView.ViewHolder(binding.root)    override fun onCreateViewHolder(        parent: ViewGroup,        viewType: Int    ): ListEventsAdapter.ListEventViewHolder {        return ListEventViewHolder(            ItemEventBinding.inflate(                LayoutInflater.from(parent.context),                parent,                false            )        )    }    override fun onBindViewHolder(holder: ListEventsAdapter.ListEventViewHolder, position: Int) {        holder.binding.apply {            holder.itemView.setOnClickListener {                onItemClickCallBack.onItemClicked(listEven[position])            }            tvNameEvent.text = listEven[position].name            tvDate.text = Constanta.formatDate(listEven[position].createdAt, TimeZone.getDefault().id)            tvDesc.text = listEven[position].deskripsi            Glide.with(ivEventList.context)                .load(listEven[position].image_poster)                .fitCenter()                .into(ivEventList)        }    }    override fun getItemCount(): Int = listEven.size    interface OnItemClickCallback {        fun onItemClicked(data: Greevents)    }}