package com.example.capstone.diimport android.content.Contextimport androidx.datastore.core.DataStoreimport androidx.datastore.preferences.core.Preferencesimport androidx.datastore.preferences.preferencesDataStoreimport com.example.capstone.api.ApiConfigimport com.example.capstone.data.AppRepositoryimport com.example.capstone.preference.PreferenceLoginval Context.dataStore: DataStore<Preferences> by preferencesDataStore("storiesin")object Injection {    fun provideRepository(context: Context): AppRepository{        val prefe = PreferenceLogin(context)        val apiService = ApiConfig.getApiService()        return AppRepository.getInstance(prefe, apiService)    }}