package com.example.capstone.modelimport android.os.Parcelableimport kotlinx.parcelize.Parcelize@Parcelizedata class ResponListJoinEvent(    val msg: String,    val status: Int,    val data: ArrayList<DetailJoin>) : Parcelable@Parcelizedata class DetailJoin(    val id: Int,    val event_id: Int,    val user_id: Int,    val nama: String,    val alamat: String,    val phone: String,    val createdAt: String,    val updatedAt: String,    val event : event,    val user: user) : Parcelable@Parcelizedata class event(    val id: Int,    val name: String,    val date: String,    val image_poster: String,    val image_surat: String,    val location: String,    val deskripsi: String,    val latitude: Double,    val longitude: Double,    val createdBy: String,    val author: String,    val contact_person: String,//    val createdAt: String,//    val updatedAt: String) : Parcelable@Parcelizedata class user(    val id: Int,    val name: String,    val email: String) : Parcelable