package com.example.capstone.modelimport android.os.Parcelableimport kotlinx.parcelize.Parcelize@Parcelizedata class ResponseComment(    val msg: String,    val status: Int,    val data: ArrayList<ComentDetail>) : Parcelable@Parcelizedata class ComentDetail(    val id: Int,    val event_id: Int,    val name: String,    val user_id: Int,    val image_profile: String,    val commentar: String,    val date: String,    val createdAt: String,    val updatedAt: String) : Parcelabledata class ResponseDetailComment(    val msg: String,    val status: Int,    val data: DetailComment)data class DetailComment(    val id: Int,    val event_id: Int,    val user_id: Int,    val commentar: String,    val createdAt: String,    val updatedAt: String,    val user: UserComment)data class UserComment(    val name: String,    val image_profile: String)data class ResponseDeleteComment(    val msg: String,    val status: Int,    val data: Int)data class ResponseUpdateComment(    val msg: String,    val status: Int,    val data: Int)