package com.example.capstone.model.event_modelimport com.example.capstone.model.userdata class ResponseDetailEvent(    val msg: String,    val status: Int,    val data: DetailEvent)data class DetailEvent(    val id: Int,    val name: String,    val date: String,    val image_poster: String,    val image_surat: String,    val location: String,    val deskripsi: String,    val latitude: Double,    val longitude: Double,    val createdBy: String,    val author: String,    val contact_person: String,    val email: String,    val createdAt: String,    val updatedAt: String,    val comments: ArrayList<Comment>)data class ResponseDeleteEvent(    val msg: String,    val status: Int,    val data: Int)data class ResponseUpdateEvent(    val msg: String,    val status: Int,    val data: Int)data class Comment(    val id: Int,    val event_id: Int,    val user_id: Int,    val commentar: String,    val date: String,    val createdAt: String,    val updatedAt: String,    val user: users)data class users(    val name: String,    val image_profile: String)