package com.example.capstone.ui.daftar_eventimport androidx.lifecycle.ViewModelimport com.example.capstone.data.AppRepositoryclass DaftarEventViewModel(private val appRepository: AppRepository) : ViewModel() {    fun joinEvent(        event_id: Int,        nama: String,        alamat: String,        phone: String    ) = appRepository.joinEvent(event_id, nama, alamat, phone)}