package com.example.capstone.ui.detail_commentimport androidx.lifecycle.ViewModelimport com.example.capstone.data.AppRepositoryclass DetailCommentViewModel(val appRepository: AppRepository) : ViewModel() {    fun getCommentById(id: Int) = appRepository.getCommentById(id)    fun deleteComment(id: Int) = appRepository.deleteComment(id)    fun updateComment(id: Int, comment: String) = appRepository.updateComment(id, comment)}