package com.example.capstone.ui.detail_eventimport androidx.lifecycle.ViewModelimport com.example.capstone.data.AppRepositoryimport okhttp3.MultipartBodyimport okhttp3.RequestBodyclass DetailEventViewModel(private val appRepository: AppRepository): ViewModel() {    fun getEventById(id: Int) = appRepository.getEventById(id)    fun deleteMyPost(id: Int) = appRepository.deleteEvent(id)    fun updateEvent(        id: Int,        name: RequestBody,        date: RequestBody,        image: MultipartBody.Part,        pdf_file: MultipartBody.Part,        location: RequestBody,        deskripsi: RequestBody,        lat: Double,        lon: Double,        author: RequestBody,        email: RequestBody,        contact: RequestBody    ) = appRepository.updateEvent(id, name, date,image,pdf_file, location, deskripsi, lat, lon, author, email, contact)    fun updateEventWithouImage(        id: Int,        name: RequestBody,        date: RequestBody,        location: RequestBody,        deskripsi: RequestBody,        lat: Double,        lon: Double,        author: RequestBody,        email: RequestBody,        contact: RequestBody    ) = appRepository.updateEventWithoutImage(id, name, date, location, deskripsi, lat, lon, author, email, contact)    fun updateEventWIthoutLocation(        id: Int,        name: RequestBody,        date: RequestBody,        image: MultipartBody.Part,        pdf_file: MultipartBody.Part,        location: RequestBody,        deskripsi: RequestBody,        author: RequestBody,        email: RequestBody,        contact: RequestBody    ) =appRepository.updateEventWithoutLocation(id, name, date, image, pdf_file, location, deskripsi, author, email, contact)    fun updateEventWIthoutImageLocation(        id: Int,        name: RequestBody,        date: RequestBody,        location: RequestBody,        deskripsi: RequestBody,        author: RequestBody,        email: RequestBody,        contact: RequestBody    ) =appRepository.updateEventWithoutImageLocation(id, name, date,  location, deskripsi, author, email, contact)}